var fs = require('fs');
var UploadError = require('./upload-error');
var ErrorCodes = require('./error-codes');
var unsupportedError = function unsupportedError() {
  throw new UploadError('filesystem operations are not permitted in a browser', ErrorCodes.INVALID_OPTIONS);
};
var stat = unsupportedError;
var readdir = unsupportedError;
var createReadStream = unsupportedError;

// fs module is not supported in browsers
if (fs) {
  // doing this manually and not using promisify to support older
  // versions of node
  stat = function stat(path) {
    return new Promise(function (res, rej) {
      fs.stat(path, function (err, stats) {
        if (err) {
          rej(err);
          return;
        }
        res(stats);
      });
    });
  };
  readdir = function readdir(path) {
    return new Promise(function (res, rej) {
      fs.readdir(path, function (err, result) {
        if (err) {
          rej(err);
          return;
        }
        res(result);
      });
    });
  };
  createReadStream = fs.createReadStream;
}
module.exports = {
  stat: stat,
  readdir: readdir,
  createReadStream: createReadStream
};